import React from "react";

const SalesList = ({ sales }) => {
    return (
        <table className="min-w-full border text-sm">
            <thead className="bg-gray-100 text-left">
                <tr>
                    <th className="py-2 px-4 border-b">ID</th>
                    <th className="py-2 px-4 border-b">Name</th>
                    <th className="py-2 px-4 border-b">Category</th>
                    <th className="py-2 px-4 border-b">Unit Price</th>
                    <th className="py-2 px-4 border-b">Quantity</th>
                    <th className="py-2 px-4 border-b">Total Price</th>
                    <th className="py-2 px-4 border-b">Date</th>
                </tr>
            </thead>
            <tbody>
                {sales.map((sale) => (
                    <tr key={sale.id}>
                        <td className="py-1 px-4 border-b">{sale.id}</td>
                        <td className="py-1 px-4 border-b">{sale.name}</td>
                        <td className="py-1 px-4 border-b">{sale.category}</td>
                        <td className="py-1 px-4 border-b">‚Çπ{sale.unitPrice}</td>
                        <td className="py-1 px-4 border-b">{sale.quantity}</td>
                        <td className="py-1 px-4 border-b">‚Çπ{sale.totalPrice}</td>
                        <td className="py-1 px-4 border-b">{sale.date}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default SalesList;
