// src/pages/SalesPage.jsx
import React, { useEffect, useState } from "react";
import RecordSaleForm from "../components/RecordSaleForm";
import SalesList from "../components/SalesList";
import CSVExportButton from "../components/ExportButton";

const SalesPage = () => {
    const [sales, setSales] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/sales") // replace with your actual API route
            .then((res) => res.json())
            .then((data) => setSales(data))
            .catch((err) => console.error("Failed to fetch sales:", err));
    }, []);

    const headers = [
        { label: "ID", key: "id" },
        { label: "Name", key: "name" },
        { label: "Category", key: "category" },
        { label: "Unit Price", key: "unitPrice" },
        { label: "Quantity", key: "quantity" },
        { label: "Total Price", key: "totalPrice" },
        { label: "Date", key: "date" },
    ];

    return (
        <div className="p-6 space-y-6">
            <div className="flex justify-between items-center">
                <h2 className="text-2xl font-semibold">ðŸ“ˆ Sales</h2>
                <CSVExportButton
                    data={sales}
                    headers={headers}
                    filename="all_sales.csv"
                />
            </div>

            <div className="bg-white rounded-lg shadow p-4">
                <h3 className="text-lg font-medium mb-4">Record a Sale</h3>
                <RecordSaleForm />
            </div>

            <div className="bg-white rounded-lg shadow p-4">
                <h3 className="text-lg font-medium mb-4">Sales Tracker</h3>
                <SalesList sales={sales} />
            </div>
        </div>
    );
};

export default SalesPage;
