import React, { useEffect, useState } from 'react';

const SalesList = () => {
    const [sales, setSales] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/api/sales')
            .then(res => res.json())
            .then(data => {
                console.log("üü¢ Sales API response:", data);
                setSales(Array.isArray(data) ? data : []);  // fallback to [] on error
            })
            .catch((err) => {
                console.error("‚ùå Error fetching sales:", err);
                setSales([]); // fallback to empty array to avoid .map() crash
            });
    }, []);


    return (
        <div>
            <h3> Sales Tracker</h3>
            {sales.length === 0 ? (
                <p>No sales found.</p>
            ) : (
                <table border="1" cellPadding="10" style={{ width: '100%', marginTop: '1rem' }}>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Name</th>
                            <th>Category</th>
                            <th>Unit Price</th>
                            <th>Quantity</th>
                            <th>Total Price</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Array.isArray(sales) ? (
                            sales.map((sale, index) => {
                                const product = sale.Product || {};
                                return (
                                    <tr key={sale.id}>
                                        <td>{index + 1}</td>
                                        <td>{product.name || '‚ùìUnknown'}</td>
                                        <td>{product.category || '-'}</td>
                                        <td>‚Çπ{product.price || '-'}</td>
                                        <td>{sale.quantity}</td>
                                        <td>‚Çπ{sale.totalPrice}</td>
                                        <td>{new Date(sale.createdAt).toLocaleString()}</td>
                                    </tr>
                                );
                            })
                        ) : (
                            <tr><td colSpan="7">‚ö†Ô∏è Error loading sales data</td></tr>
                        )}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default SalesList;
