// src/pages/SalesSummary.jsx
import React, { useEffect, useState } from "react";
import { Card, CardContent } from "../components/ui/card";

const SalesSummary = () => {
    const [salesData, setSalesData] = useState({
        totalSales: 0,
        totalRevenue: 0,
        totalQuantitySold: 0,
    });

    useEffect(() => {
        fetch("http://localhost:5000/api/sales/summary")
            .then((res) => res.json())
            .then((data) => setSalesData(data))
            .catch((err) => console.error("❌ Failed to fetch sales summary:", err));
    }, []);

    return (
        <div className="grid grid-cols-1 gap-4 md:grid-cols-2 lg:grid-cols-3 p-4">
            <Card>
                <CardContent>
                    <h3 className="text-2xl font-semibold mb-2">Total Sales</h3>
                    <p className="text-lg">{salesData.totalSales}</p>
                </CardContent>
            </Card>
            <Card>
                <CardContent>
                    <h3 className="text-2xl font-semibold mb-2">Total Revenue</h3>
                    <p className="text-lg">₹ {salesData.totalRevenue}</p>
                </CardContent>
            </Card>
            <Card>
                <CardContent>
                    <h3 className="text-2xl font-semibold mb-2">Total Quantity Sold</h3>
                    <p className="text-lg">{salesData.totalQuantitySold}</p>
                </CardContent>
            </Card>
        </div>
    );
};

export default SalesSummary;
