import React, { useEffect, useState } from 'react';
import LowStockAlert from '../components/LowStockAlert';
import { toast } from 'react-toastify';



const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [editingProduct, setEditingProduct] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        fetch('http://localhost:5000/api/products')
            .then((res) => res.json())
            .then((data) => setProducts(data))
            .catch((err) => console.error('❌ Error fetching products:', err));
    }, []);
    const filteredProducts = products.filter((product) =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.category.toLowerCase().includes(searchTerm.toLowerCase())
    );

    //  DELETE handler
    const handleDelete = async (id) => {
        const confirm = window.confirm("⚠️ Are you sure you want to delete this product?");
        if (!confirm) return;

        try {
            await fetch(`http://localhost:5000/api/products/${id}`, {
                method: 'DELETE',
            });
            setProducts(products.filter((p) => p.id !== id));
            toast.success("🗑️ Product deleted successfully");
        } catch (err) {
            console.error(' Failed to delete product', err);
            toast.error(" Error deleting product");
        }
    };


    //  EDIT handler
    const handleEdit = (product) => {
        setEditingProduct(product);
        setShowModal(true);
    };

    const handleUpdate = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/products/${editingProduct.id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(editingProduct),
            });

            if (res.ok) {
                const updatedList = products.map((p) =>
                    p.id === editingProduct.id ? editingProduct : p
                );
                setProducts(updatedList);
                toast.success(" Product updated");
                setShowModal(false);
            } else {
                toast.error(" Failed to update");
            }
        } catch (err) {
            console.error(err);
            toast.error(" Error updating product");
        }
    };



    return (
        <div>
            <h3> Product Inventory</h3>
            <div>
                <LowStockAlert />
                {/* Rest of your product inventory table */}
            </div>
            <h2>Search</h2>
            <input
                type="text"
                placeholder=" Search by name or category..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                    padding: '8px',
                    marginBottom: '15px',
                    width: '300px',
                    borderRadius: '4px',
                    border: '1px solid #ccc'
                }}
            />


            {products.length === 0 ? (
                <p>No products found.</p>

            ) : (
                <table className="min-w-full border text-sm">
                    <thead className="bg-gray-100 text-left">
                        <tr>
                            <th className="py-2 px-4 border-b">ID</th>
                            <th className="py-2 px-4 border-b">Name</th>
                            <th className="py-2 px-4 border-b">Category</th>
                            <th className="py-2 px-4 border-b">Price (₹)</th>
                            <th className="py-2 px-4 border-b">Quantity</th>
                            <th className="py-2 px-4 border-b">Actions</th>
                        </tr>
                    </thead>
                    <tbody>

                        {filteredProducts.map((p, index) => (

                            <tr key={p.id}>
                                <td>{index + 1}</td>
                                <td>{p.name}</td>
                                <td>{p.category}</td>
                                <td>{p.price}</td>
                                <td>
                                    {p.quantity}{' '}
                                    {p.quantity < 10 && (
                                        <span style={{ color: 'red', marginLeft: '10px' }}>
                                            🔻 Low Stock
                                        </span>
                                    )}


                                </td>
                                <td>
                                    <button onClick={() => handleEdit(p)}>✏️ Edit</button>
                                    <button onClick={() => handleDelete(p.id)} style={{ marginLeft: '10px' }}>
                                        🗑️ Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                    {showModal && (
                        <div style={modalStyle}>
                            <h3>Edit Product</h3>
                            <label>
                                Name:
                                <input
                                    type="text"
                                    value={editingProduct.name}
                                    onChange={(e) => setEditingProduct({ ...editingProduct, name: e.target.value })}
                                />
                            </label>
                            <br />
                            <label>
                                Category:
                                <input
                                    type="text"
                                    value={editingProduct.category}
                                    onChange={(e) => setEditingProduct({ ...editingProduct, category: e.target.value })}
                                />
                            </label>
                            <br />
                            <label>
                                Price:
                                <input
                                    type="number"
                                    value={editingProduct.price}
                                    onChange={(e) => setEditingProduct({ ...editingProduct, price: e.target.value })}
                                />
                            </label>
                            <br />
                            <label>
                                Quantity:
                                <input
                                    type="number"
                                    value={editingProduct.quantity}
                                    onChange={(e) => setEditingProduct({ ...editingProduct, quantity: e.target.value })}
                                />
                            </label>
                            <br /><br />
                            <button onClick={handleUpdate}> Save</button>
                            <button onClick={() => setShowModal(false)} style={{ marginLeft: '10px' }}>❌ Cancel</button>
                        </div>
                    )}

                </table>

            )}
        </div>
    );
};




const modalStyle = {
    position: 'fixed',
    top: '20%',
    left: '35%',
    backgroundColor: '#fff',
    padding: '20px',
    border: '2px solid #000',
    borderRadius: '8px',
    zIndex: 9999,
    boxShadow: '0 0 10px rgba(0,0,0,0.3)',
};


export default ProductList;
