import React, { useEffect, useState } from 'react';
import { SalesChart, SalesWeeklyChart } from '../components/SalesChart';
const SalesOverviewPage = () => {
    const [salesData, setSalesData] = useState({
        totalSales: 0,
        totalRevenue: 0,
        totalQuantitySold: 0
    });

    useEffect(() => {
        fetch('http://localhost:5000/api/sales/summary')
            .then((res) => res.json())
            .then((data) => setSalesData(data))
            .catch((err) => console.error('❌ Failed to fetch sales summary:', err));
    }, []);

    return (
        <div style={{ padding: '2rem' }}>
            <h2> Sales Overview</h2>

            {/* Download Button */}
            <div style={{ marginTop: '1rem', marginBottom: '2rem' }}>
                <a
                    href="http://localhost:5000/api/sales/export/csv"
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#007bff',
                        color: 'white',
                        borderRadius: '5px',
                        textDecoration: 'none',
                        fontWeight: 'bold',
                        boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
                    }}
                >
                    Download Sales Data as CSV
                </a>
            </div>

            {/* Cards */}
            <div
                style={{
                    display: 'flex',
                    gap: '2rem',
                    justifyContent: 'center',
                    flexWrap: 'wrap'
                }}
            >
                <div style={cardStyle}>
                    <h3>Total Sales</h3>
                    <p>{salesData.totalSales}</p>
                </div>

                <div style={cardStyle}>
                    <h3>Total Revenue</h3>
                    <p>₹ {salesData.totalRevenue}</p>
                </div>

                <div style={cardStyle}>
                    <h3>Total Quantity Sold</h3>
                    <p>{salesData.totalQuantitySold}</p>
                </div>
            </div>
            <SalesChart />
            <SalesWeeklyChart />
        </div>
    );
};

const cardStyle = {
    backgroundColor: '#f0f0f0',
    padding: '1.5rem',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    minWidth: '200px',
    textAlign: 'center'
};

export default SalesOverviewPage;
