// src/pages/Sales.jsx
import React, { useEffect, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { SalesChart, SalesWeeklyChart } from "@/components/SalesChart";

const Sales = () => {
    const [salesData, setSalesData] = useState({
        totalSales: 0,
        totalRevenue: 0,
        totalQuantitySold: 0,
    });

    useEffect(() => {
        fetch("http://localhost:5000/api/sales/summary")
            .then((res) => res.json())
            .then((data) => setSalesData(data))
            .catch((err) => console.error("❌ Failed to fetch sales summary:", err));
    }, []);

    return (
        <div className="p-4 space-y-6">
            {/* Download Button */}
            <div>
                <a
                    href="http://localhost:5000/api/sales/export/csv"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-block px-4 py-2 bg-blue-600 text-white rounded-md shadow-md hover:bg-blue-700 transition"
                >
                    Download Sales Data as CSV
                </a>
            </div>

            {/* Summary Cards */}
            <div className="grid grid-cols-1 gap-4 md:grid-cols-2 lg:grid-cols-3">
                <Card>
                    <CardContent>
                        <h3 className="text-2xl font-semibold mb-2">Total Sales</h3>
                        <p className="text-lg">{salesData.totalSales}</p>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <h3 className="text-2xl font-semibold mb-2">Total Revenue</h3>
                        <p className="text-lg">₹ {salesData.totalRevenue}</p>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <h3 className="text-2xl font-semibold mb-2">Total Quantity Sold</h3>
                        <p className="text-lg">{salesData.totalQuantitySold}</p>
                    </CardContent>
                </Card>
            </div>

            {/* Charts */}
            <div className="grid grid-cols-1 gap-6">
                <SalesChart />
                <SalesWeeklyChart />
            </div>
        </div>
    );
};

export default Sales;
