import React from 'react';
import './index.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import ProductsPage from './pages/ProductsPage';
import SalesPage from './pages/SalesPage';
import SalesSummary from './pages/SalesSummary';
import Dashboard from './pages/Dashboard';



<h1 className="text-4xl font-bold text-blue-600">Tailwind is working!</h1>

function App() {
  return (
    <Router>
      <div style={{ padding: '20px' }}>
        <h1> Inventory Management System</h1>

        <nav style={{ marginBottom: '20px' }}>
          <Link to="/products" style={{ marginRight: '15px' }}> Products</Link>
          <Link to="/sales" style={{ marginRight: '15px' }}> Sales</Link>
          <Link to="/sales-summary" style={{ marginRight: '15px' }}> Sales Summary</Link>
        </nav>

        <ToastContainer position="top-right" autoClose={3000} />

        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/products" element={<ProductsPage />} />
          <Route path="/sales" element={<SalesPage />} />
          <Route path="/sales-summary" element={<SalesSummary />} />
          {/* <Route path="*" element={<p>Welcome! Please select a module from above.</p>} /> */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
