import React, { useEffect, useState } from "react";
import { Card, CardContent } from "/components/ui/card";
import { SalesChart, SalesWeeklyChart } from "../components/SalesChart";
import CSVExportButton from "../components/ExportButton";

const SalesSummary = () => {
    const [salesData, setSalesData] = useState({
        totalSales: 0,
        totalRevenue: 0,
        totalQuantitySold: 0,
    });

    useEffect(() => {
        fetch("http://localhost:5000/api/sales/summary")
            .then((res) => res.json())
            .then((data) => setSalesData(data))
            .catch((err) => console.error("‚ùå Failed to fetch sales summary:", err));
    }, []);

    const summaryHeaders = [
        { label: "Total Sales", key: "totalSales" },
        { label: "Total Revenue", key: "totalRevenue" },
        { label: "Total Quantity Sold", key: "totalQuantitySold" },
    ];

    const summaryData = [salesData]; // wrap in array for export

    return (
        <div className="p-4 space-y-6">
            {/* Header with Export Button */}
            <div className="flex justify-between items-center">
                <h2 className="text-2xl font-semibold">üìä Sales Summary</h2>
                <CSVExportButton
                    data={summaryData}
                    headers={summaryHeaders}
                    filename="sales_summary.csv"
                />
            </div>

            {/* Summary Cards */}
            <div className="grid grid-cols-1 gap-4 md:grid-cols-2 lg:grid-cols-3">
                <Card>
                    <CardContent>
                        <h3 className="text-2xl font-semibold mb-2">Total Sales</h3>
                        <p className="text-lg">{salesData.totalSales}</p>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <h3 className="text-2xl font-semibold mb-2">Total Revenue</h3>
                        <p className="text-lg">‚Çπ {salesData.totalRevenue}</p>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <h3 className="text-2xl font-semibold mb-2">Total Quantity Sold</h3>
                        <p className="text-lg">{salesData.totalQuantitySold}</p>
                    </CardContent>
                </Card>
            </div>

            {/* Charts */}
            <div className="grid grid-cols-1 gap-6 lg:grid-cols-2">
                <Card>
                    <CardContent>
                        <h3 className="text-xl font-medium mb-4">Monthly Sales Overview</h3>
                        <SalesChart />
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <h3 className="text-xl font-medium mb-4">Weekly Sales Breakdown</h3>
                        <SalesWeeklyChart />
                    </CardContent>
                </Card>
            </div>
        </div>
    );
};

export default SalesSummary;
