// src/components/Sidebar.jsx
import React from "react";
import { NavLink } from "react-router-dom";
import {
    LayoutDashboard,
    Package,
    TrendingUp,
    BarChart2,
    Settings
} from "lucide-react";

const navItems = [
    { to: "/", label: "Dashboard", icon: <LayoutDashboard size={20} /> },
    { to: "/products", label: "Products", icon: <Package size={20} /> },
    { to: "/sales", label: "Sales", icon: <TrendingUp size={20} /> },
    { to: "/sales-summary", label: "Summary", icon: <BarChart2 size={20} /> },
    { to: "/settings", label: "Settings", icon: <Settings size={20} /> },
];

const Sidebar = () => {
    return (
        <div className="w-64 bg-blue-900 text-white min-h-screen p-6 shadow-lg">
            <h2 className="text-2xl font-bold mb-8 tracking-wide">ðŸ“¦ InventorySys</h2>
            <nav className="space-y-2">
                {navItems.map((item) => (
                    <NavLink
                        key={item.to}
                        to={item.to}
                        className={({ isActive }) =>
                            `flex items-center space-x-3 px-4 py-2 rounded-md transition-colors duration-200 ${isActive ? "bg-blue-700 text-yellow-300" : "hover:bg-blue-800 hover:text-yellow-200"
                            }`
                        }
                    >
                        {item.icon}
                        <span>{item.label}</span>
                    </NavLink>
                ))}
            </nav>
        </div>
    );
};

export default Sidebar;
