            <nav className="space-y-4">
                <NavLink to="/" className="block hover:text-yellow-300">üè† Dashboard</NavLink>
                <NavLink to="/products" className="block hover:text-yellow-300">üì¶ Products</NavLink>
                <NavLink to="/sales" className="block hover:text-yellow-300">üìà Sales</NavLink>
                <NavLink to="/summary" className="block hover:text-yellow-300">üìä Summary</NavLink>
                <NavLink to="/settings" className="block hover:text-yellow-300">‚öôÔ∏è Settings</NavLink>
            </nav>
        </div>
    );
};

export default Sidebar;
