// src/components/Layout.jsx
import React from "react";
import Sidebar from "../components/ui/Sidebar";

const Layout = ({ children }) => {
    return (
        <div className="flex">
            <Sidebar />
            <main className="flex-1 bg-gray-100 min-h-screen p-6">
                {children}
            </main>
        </div>
    );
};

export default Layout;
